   [34]={next_id=35, abreast_id={}, pType =1, cg_type =0, dialog=1, msg="大人不必如此客气，我们只希望从目前的困境中脱离出去，至于之后怎么办，恐怕还没有答案。", msg_color="", msg_size=0, talker="弗丽达", audio="", img="arts/ui/bg/story/storybg_704.jpg", video_url="", music_id=0, background_music="", model_tid={{ 2, "7144", 2, 0},{ 1, "7143", 2, 0}}, model_pos={{ 0,0,0},{ 0,0,0}}, model_move_pos={}, model_exit={}, update_model={{ 2,0},{ 1,1}}, role_act={}, head_path="", flash_time=0, shake_ani=0, grad_time=0, is_memory=0, roll_str="",roll_bg ="", static_pic ="", prefab_path ="", runTalkTime =0, destoryTime =0, clickTimes =0, effPos={ 0,0,0},effUrl="", waitIDs = {}},
