   [5]={next_id=6, abreast_id={}, pType =1, cg_type =10, dialog=1, msg="黑色的树木突兀地生长在泥土上，没有枝叶，仿佛一根根地标。极光沿着树木流淌而下，如水般浸过了我的脚下，每一次挪动脚步，这极光都会被踢散，转而又重新汇聚在一起。", msg_color="", msg_size=0, talker="", audio="", img="arts/ui/bg/story/storybg_1001.jpg", video_url="", music_id=0, background_music="", model_tid={}, model_pos={}, model_move_pos={}, model_exit={}, update_model={}, role_act={}, head_path="", flash_time=0, shake_ani=0, grad_time=0, is_memory=0, roll_str="",roll_bg ="", static_pic ="", prefab_path ="", runTalkTime =0, destoryTime =0, clickTimes =0, effPos={ 0,0,0},effUrl="", waitIDs = {}},
