   [8]={next_id=9, abreast_id={}, pType =1, cg_type =0, dialog=1, msg="我知道你有想问的问题，说吧。", msg_color="", msg_size=0, talker="雷奥妮", audio="", img="arts/ui/bg/story/storybg_207.jpg", video_url="", music_id=0, background_music="", model_tid={{ 2, "7144", 2, 0}}, model_pos={{ 0,0,0}}, model_move_pos={}, model_exit={}, update_model={{ 2,1}}, role_act={}, head_path="", flash_time=0, shake_ani=0, grad_time=0, is_memory=0, roll_str="",roll_bg ="", static_pic ="", prefab_path ="", runTalkTime =0, destoryTime =0, clickTimes =0, effPos={ 0,0,0},effUrl="", waitIDs = {}},
   [9]={next_id=10, abreast_id={}, pType =1, cg_type =0, dialog=1, msg="老师，为什么卢卡斯先生会这么反对呢？那座工厂是什么人开办的？", msg_color="", msg_size=0, talker="奥莉维亚", audio="", img="arts/ui/bg/story/storybg_207.jpg", video_url="", music_id=0, background_music="", model_tid={{ 2, "7144", 2, 0},{ 3, "4108_h", 0, 0}}, model_pos={{ 0,0,0},{ 0,0,0}}, model_move_pos={}, model_exit={}, update_model={{ 2,0},{ 3,1}}, role_act={}, head_path="", flash_time=0, shake_ani=0, grad_time=0, is_memory=0, roll_str="",roll_bg ="", static_pic ="", prefab_path ="", runTalkTime =0, destoryTime =0, clickTimes =0, effPos={ 0,0,0},effUrl="", waitIDs = {}},
   [10]={next_id=11, abreast_id={}, pType =1, cg_type =0, dialog=1, msg="他会反对是正常的——那座工厂是冶新会的产业。", msg_color="", msg_size=0, talker="雷奥妮", audio="", img="arts/ui/bg/story/storybg_207.jpg", video_url="", music_id=0, background_music="", model_tid={{ 2, "7144", 2, 0},{ 3, "4108_h", 0, 0}}, model_pos={{ 0,0,0},{ 0,0,0}}, model_move_pos={}, model_exit={}, update_model={{ 2,1},{ 3,0}}, role_act={}, head_path="", flash_time=0, shake_ani=0, grad_time=0, is_memory=0, roll_str="",roll_bg ="", static_pic ="", prefab_path ="", runTalkTime =0, destoryTime =0, clickTimes =0, effPos={ 0,0,0},effUrl="", waitIDs = {}},
   [11]={next_id=12, abreast_id={}, pType =1, cg_type =0, dialog=1, msg="什么？！那岂不是——", msg_color="", msg_size=0, talker="奥莉维亚", audio="", img="arts/ui/bg/story/storybg_207.jpg", video_url="", music_id=0, background_music="", model_tid={{ 2, "7144", 2, 0},{ 3, "4108_h", 0, 0}}, model_pos={{ 0,0,0},{ 0,0,0}}, model_move_pos={}, model_exit={}, update_model={{ 2,0},{ 3,1}}, role_act={}, head_path="", flash_time=0, shake_ani=0, grad_time=0, is_memory=0, roll_str="",roll_bg ="", static_pic ="", prefab_path ="", runTalkTime =0, destoryTime =0, clickTimes =0, effPos={ 0,0,0},effUrl="", waitIDs = {}},
   [12]={next_id=13, abreast_id={}, pType =1, cg_type =0, dialog=1, msg="是的，就是在国内制造了那起严重事故的冶新会。", msg_color="", msg_size=0, talker="雷奥妮", audio="", img="arts/ui/bg/story/storybg_207.jpg", video_url="", music_id=0, background_music="", model_tid={{ 2, "7144", 2, 0},{ 3, "4108_h", 0, 0}}, model_pos={{ 0,0,0},{ 0,0,0}}, model_move_pos={}, model_exit={}, update_model={{ 2,1},{ 3,0}}, role_act={}, head_path="", flash_time=0, shake_ani=0, grad_time=0, is_memory=0, roll_str="",roll_bg ="", static_pic ="", prefab_path ="", runTalkTime =0, destoryTime =0, clickTimes =0, effPos={ 0,0,0},effUrl="", waitIDs = {}},
   [13]={next_id=14, abreast_id={}, pType =1, cg_type =0, dialog=1, msg="我记得当时的报道里说，他们本来打算在国内的科技博览会上展出一种新型源晶节能装置。", msg_color="", msg_size=0, talker="奥莉维亚", audio="", img="arts/ui/bg/story/storybg_207.jpg", video_url="", music_id=0, background_music="", model_tid={{ 2, "7144", 2, 0},{ 3, "4108_h", 0, 0}}, model_pos={{ 0,0,0},{ 0,0,0}}, model_move_pos={}, model_exit={}, update_model={{ 2,0},{ 3,1}}, role_act={}, head_path="", flash_time=0, shake_ani=0, grad_time=0, is_memory=0, roll_str="",roll_bg ="", static_pic ="", prefab_path ="", runTalkTime =0, destoryTime =0, clickTimes =0, effPos={ 0,0,0},effUrl="", waitIDs = {}},
