   [16]={next_id=17, abreast_id={}, pType =1, cg_type =0, dialog=1, msg="冶新会一直不遗余力地想要将新技术带入克罗瓦德。", msg_color="", msg_size=0, talker="雷奥妮", audio="", img="arts/ui/bg/story/storybg_207.jpg", video_url="", music_id=0, background_music="", model_tid={{ 2, "7144", 2, 0}}, model_pos={{ 0,0,0}}, model_move_pos={}, model_exit={}, update_model={{ 2,1}}, role_act={}, head_path="", flash_time=0, shake_ani=0, grad_time=0, is_memory=0, roll_str="",roll_bg ="", static_pic ="", prefab_path ="", runTalkTime =0, destoryTime =0, clickTimes =0, effPos={ 0,0,0},effUrl="", waitIDs = {}},
   [17]={next_id=18, abreast_id={}, pType =1, cg_type =0, dialog=1, msg="那起事故之前，他们的做法虽然招来过许多非议，但整体上议会并没有阻碍他们的研究。", msg_color="", msg_size=0, talker="雷奥妮", audio="", img="arts/ui/bg/story/storybg_207.jpg", video_url="", music_id=0, background_music="", model_tid={{ 2, "7144", 2, 0}}, model_pos={{ 0,0,0}}, model_move_pos={}, model_exit={}, update_model={{ 2,1}}, role_act={}, head_path="", flash_time=0, shake_ani=0, grad_time=0, is_memory=0, roll_str="",roll_bg ="", static_pic ="", prefab_path ="", runTalkTime =0, destoryTime =0, clickTimes =0, effPos={ 0,0,0},effUrl="", waitIDs = {}},
   [18]={next_id=19, abreast_id={}, pType =1, cg_type =0, dialog=1, msg="但是之后嘛，呵……", msg_color="", msg_size=0, talker="雷奥妮", audio="", img="arts/ui/bg/story/storybg_207.jpg", video_url="", music_id=0, background_music="", model_tid={{ 2, "7144", 2, 0}}, model_pos={{ 0,0,0}}, model_move_pos={}, model_exit={}, update_model={{ 2,1}}, role_act={}, head_path="", flash_time=0, shake_ani=0, grad_time=0, is_memory=0, roll_str="",roll_bg ="", static_pic ="", prefab_path ="", runTalkTime =0, destoryTime =0, clickTimes =0, effPos={ 0,0,0},effUrl="", waitIDs = {}},
   [19]={next_id=20, abreast_id={}, pType =1, cg_type =0, dialog=1, msg="可是，我听说那起事故只是意外——", msg_color="", msg_size=0, talker="奥莉维亚", audio="", img="arts/ui/bg/story/storybg_207.jpg", video_url="", music_id=0, background_music="", model_tid={{ 2, "4108_h", 0, 0}}, model_pos={{ 0,0,0}}, model_move_pos={}, model_exit={}, update_model={{ 2,1}}, role_act={}, head_path="", flash_time=0, shake_ani=0, grad_time=0, is_memory=0, roll_str="",roll_bg ="", static_pic ="", prefab_path ="", runTalkTime =0, destoryTime =0, clickTimes =0, effPos={ 0,0,0},effUrl="", waitIDs = {}},
