   [21]={next_id=22, abreast_id={}, pType =1, cg_type =0, dialog=1, msg="冶新会的人一直执着于源晶科技，从来没有考虑到这样会毁掉多少人的生活。", msg_color="", msg_size=0, talker="雷奥妮", audio="", img="arts/ui/bg/story/storybg_207.jpg", video_url="", music_id=0, background_music="", model_tid={{ 2, "7144", 2, 0}}, model_pos={{ 0,0,0}}, model_move_pos={}, model_exit={}, update_model={{ 2,1}}, role_act={}, head_path="", flash_time=0, shake_ani=0, grad_time=0, is_memory=0, roll_str="",roll_bg ="", static_pic ="", prefab_path ="", runTalkTime =0, destoryTime =0, clickTimes =0, effPos={ 0,0,0},effUrl="", waitIDs = {}},
   [22]={next_id=23, abreast_id={}, pType =1, cg_type =0, dialog=1, msg="一旦他们的构想成为现实，很多人会失去工作，国内的能源压力也会变得更大。", msg_color="", msg_size=0, talker="雷奥妮", audio="", img="arts/ui/bg/story/storybg_207.jpg", video_url="", music_id=0, background_music="", model_tid={{ 2, "7144", 2, 0}}, model_pos={{ 0,0,0}}, model_move_pos={}, model_exit={}, update_model={{ 2,1}}, role_act={}, head_path="", flash_time=0, shake_ani=0, grad_time=0, is_memory=0, roll_str="",roll_bg ="", static_pic ="", prefab_path ="", runTalkTime =0, destoryTime =0, clickTimes =0, effPos={ 0,0,0},effUrl="", waitIDs = {}},
