   [37]={next_id=38, abreast_id={}, pType =1, cg_type =0, dialog=1, msg="至于卢卡斯说的那些话……你也不用多想，我回头会单独找他。", msg_color="", msg_size=0, talker="雷奥妮", audio="", img="arts/ui/bg/story/storybg_207.jpg", video_url="", music_id=0, background_music="", model_tid={{ 2, "7144", 2, 0}}, model_pos={{ 0,0,0}}, model_move_pos={}, model_exit={}, update_model={{ 2,1}}, role_act={}, head_path="", flash_time=0, shake_ani=0, grad_time=0, is_memory=0, roll_str="",roll_bg ="", static_pic ="", prefab_path ="", runTalkTime =0, destoryTime =0, clickTimes =0, effPos={ 0,0,0},effUrl="", waitIDs = {}},
   [38]={next_id=39, abreast_id={}, pType =1, cg_type =0, dialog=1, msg="老师，我是不是在无意识的情况下惹卢卡斯先生生气了？", msg_color="", msg_size=0, talker="奥莉维亚", audio="", img="arts/ui/bg/story/storybg_207.jpg", video_url="", music_id=0, background_music="", model_tid={{ 2, "7144", 2, 0},{ 3, "4108_h", 0, 0}}, model_pos={{ 0,0,0},{ 0,0,0}}, model_move_pos={}, model_exit={}, update_model={{ 2,0},{ 3,1}}, role_act={}, head_path="", flash_time=0, shake_ani=0, grad_time=0, is_memory=0, roll_str="",roll_bg ="", static_pic ="", prefab_path ="", runTalkTime =0, destoryTime =0, clickTimes =0, effPos={ 0,0,0},effUrl="", waitIDs = {}},
   [39]={next_id=40, abreast_id={}, pType =1, cg_type =0, dialog=1, msg="我知道我出身不高，有时候说的话可能是比较浅薄……", msg_color="", msg_size=0, talker="奥莉维亚", audio="", img="arts/ui/bg/story/storybg_207.jpg", video_url="", music_id=0, background_music="", model_tid={{ 2, "7144", 2, 0},{ 3, "4108_h", 0, 0}}, model_pos={{ 0,0,0},{ 0,0,0}}, model_move_pos={}, model_exit={}, update_model={{ 2,0},{ 3,1}}, role_act={}, head_path="", flash_time=0, shake_ani=0, grad_time=0, is_memory=0, roll_str="",roll_bg ="", static_pic ="", prefab_path ="", runTalkTime =0, destoryTime =0, clickTimes =0, effPos={ 0,0,0},effUrl="", waitIDs = {}},
