local CardEncyclopediaComponent = BaseClass("CardEncyclopediaComponent", Component)
local base = Component

local function Awake(self)
    base.Awake(self)

    self.numeric_component = self.__entity:GetComponent("NumericComponent")

    local encyclopediaProperty = UIPublic.GetEncyclopediaPropertyScale()

    if encyclopediaProperty[1001] ~= nil then
        self.numeric_component:Inc(NumericType.MaxHpPct, encyclopediaProperty[1001]/100)
    end

    if encyclopediaProperty[1003] ~= nil then
        self.numeric_component:Inc(NumericType.AtkPct, encyclopediaProperty[1003]/100)
    end
    
    if encyclopediaProperty[1004] ~= nil then
        self.numeric_component:Inc(NumericType.DefPct, encyclopediaProperty[1004]/100)
    end

    if encyclopediaProperty[1053] ~= nil then
        self.numeric_component:Inc(NumericType.MagicDefPct, encyclopediaProperty[1053]/100)
    end

    if encyclopediaProperty[1000] ~= nil then
        self.numeric_component:Inc(NumericType.SpeedPct, encyclopediaProperty[1000]/100)
    end
end


local function Dispose(self)
    base.Dispose(self)
end

CardEncyclopediaComponent.Awake = Awake
CardEncyclopediaComponent.Dispose = Dispose

return CardEncyclopediaComponent