local CardJadePearlComponent = BaseClass("CardJadePearlComponent", Component)
local base = Component

local function Awake(self)
    base.Awake(self)

    self.numeric_component = self.__entity:GetComponent("NumericComponent")

    local bead = self.__entity.Bead
    if bead == nil or bead == 0 then
        return
    end

    local characterBeadInfo = Z_CharacterBead[self.__entity.TemplateId]
    for i = 1, bead do
        for type, info in pairs(characterBeadInfo[i]) do
            local addtype = GameUtil.GetAttrTypeBaseFromName(type)
            if addtype ~= 0 and info ~= 0 then
                self.numeric_component:Inc(addtype, info)
            end
        end
    end
end

local function Dispose(self)
    base.Dispose(self)
end

CardJadePearlComponent.Awake = Awake
CardJadePearlComponent.Dispose = Dispose

return CardJadePearlComponent