local PlayerComponent = BaseClass("PlayerComponent", Component)
local base = Component

local function Awake(self)
    base.Awake(self)
    self.Player = nil
    self.time = 0
end

local function Dispose(self)
    base.Dispose(self)
    self.Player:Dispose()
end

local function buffTime(self)
    self.time = TimeUtil.OSTime() + self.Player.EnergyNextTime -- TODO:还没同步时间
end

