local Post = BaseClass("Post",Entity);
local base = Entity
local this = Post;

function this.Awake(self,PostInfo)
    base.Awake(self)
    self.MailId = PostInfo.MailId
    self.Title = PostInfo.Title
    self.Msg = PostInfo.Msg
    self.FromId = PostInfo.FromId
    self.State = PostInfo.State --状态 0未读 1已读 2已领取
    self.RewardInfoList = PostInfo.RewardInfoList --1物品  2金币
    self.Time = PostInfo.Time
end

function this.Dispose(self)
    base.Dispose(self)
end


return this