local StoryLevelScore = BaseClass("StoryLevelScore",Entity);
local base = Entity

local function Awake(self,storyLevelScoreInfo, levelType)
    base.Awake(self)
    if storyLevelScoreInfo ~= nil then
        self.Difficulty = 1
        if levelType == LevelType.LevelTypeStory then
            self.Difficulty = Z_StorySection[storyLevelScoreInfo.id].Difficulty or 1--storyLevelScoreInfo.Difficulty
        elseif levelType == LevelType.LevelTypeGameEvent then
            self.Difficulty = Z_GameEventsLevel[storyLevelScoreInfo.id].Difficulty or 1--storyLevelScoreInfo.Difficulty
        end
        self.LevelId = storyLevelScoreInfo.id
        if storyLevelScoreInfo.reached_goals == nil then
            self.IsReachRewardCond1 = 0
            self.IsReachRewardCond2 = 0
            self.IsReachRewardCond3 = 0
        else
            self.IsReachRewardCond1 = storyLevelScoreInfo.reached_goals[1]
            self.IsReachRewardCond2 = storyLevelScoreInfo.reached_goals[2]
            self.IsReachRewardCond3 = storyLevelScoreInfo.reached_goals[3]
        end
        

        ----old---
        -- self.Difficulty = storyLevelScoreInfo.Difficulty
        -- self.LevelId = storyLevelScoreInfo.LevelId
        -- self.IsReachRewardCond1 = 0
        -- self.IsReachRewardCond2 = 0
        -- self.IsReachRewardCond3 = 0
    end
end

local function Dispose(self)
    base.Dispose(self)
end

StoryLevelScore.Awake = Awake
StoryLevelScore.Dispose = Dispose

return StoryLevelScore