local BattleEncyclopediaComponent = BaseClass("BattleEncyclopediaComponent", Component)
local base = Component

local function Awake(self)
    base.Awake(self)

    self.numeric_component = self.__entity:GetComponent("NumericComponent")

    local encyclopediaProperty = self.__entity.EncyclopediaTable

    if encyclopediaProperty == nil then
        return
    end

    if encyclopediaProperty["hp_enhance"] ~= nil then
        self.numeric_component:Inc(NumericType.MaxHpPct, encyclopediaProperty["hp_enhance"]/100)
    end

    if encyclopediaProperty["atk_enhance"] ~= nil then
        self.numeric_component:Inc(NumericType.AtkPct, encyclopediaProperty["atk_enhance"]/100)
    end
    
    if encyclopediaProperty["def_enhance"] ~= nil then
        self.numeric_component:Inc(NumericType.DefPct, encyclopediaProperty["def_enhance"]/100)
    end

    if encyclopediaProperty["mdef_enhance"] ~= nil then
        self.numeric_component:Inc(NumericType.MagicDefPct, encyclopediaProperty["mdef_enhance"]/100)
    end

    if encyclopediaProperty["speed_enhance"] ~= nil then
        self.numeric_component:Inc(NumericType.SpeedPct, encyclopediaProperty["speed_enhance"]/100)
    end
end

local function Dispose(self)
    base.Dispose(self)
end

BattleEncyclopediaComponent.Awake = Awake
BattleEncyclopediaComponent.Dispose = Dispose

return BattleEncyclopediaComponent