local FightSoul_30030 = BaseClass("FightSoul_30030",Skill)
local base = Skill

local function __init(self)
end

local function BeforeBuffAddEvent(self, buff)
    if buff.TargetUnit ~= self.SrcUnit then
        return
    end

    if not BattleUtil.IsShieldBuff(buff.BuffId) then
        return
    end

    buff.Data.ShieldValue =  math.floor(buff.Data.ShieldValue * (1 + self.Parmas[1] / 100))
end


FightSoul_30030.__init = __init
FightSoul_30030.BeforeBuffAddEvent = BeforeBuffAddEvent

return FightSoul_30030