local Skill_10252 = BaseClass("Skill_10252",Skill)
local base = Skill

local function __init(self)
end

local function OnUseActiveSkill(self, targetUnit)
    local result = ActiveSkillResult.New()

    local enemies = self:__get_enemy_all()
    local totalCount = 0
    for k, v in pairs(enemies) do
        totalCount = totalCount + v:GetComponent("BuffComponent"):BuffTypeCount(true)
    end
    local actValue = totalCount * self.Params[1]
    result.ActChange = actValue
    table.insert(result.Buffs, ActiveSkillBuff.New(BattleBuffType.AtkIncrease, self.Params[2]))

    return result
end

Skill_10252.__init = __init
Skill_10252.OnUseActiveSkill = OnUseActiveSkill

return Skill_10252