local Skill_10253 = BaseClass("Skill_10253",Skill)
local base = Skill

local function __init(self)
end

local function OnUseActiveSkill(self, targetUnit)
    local result = ActiveSkillResult.New()
    if targetUnit ~= self.SrcUnit then
        local count = self.Params[1]
        local turnCount = self.Params[2]

        local debuffTypes = {}
        for k, v in pairs(BattleBuffType) do
            if v > BattleBuffType.DebuffStart and v < BattleBuffType.DebuffEnd then
                table.insert(debuffTypes, v)
            end
        end
        for i = 1, count do
            local r = math.random(1, #debuffTypes)
            table.insert(result.Buffs, ActiveSkillBuff.New(debuffTypes[r], turnCount))
        end
    else
        self.__bc.HighReadyQueue:enqueue(self.SrcUnit)
    end
    return result
end

Skill_10253.__init = __init
Skill_10253.OnUseActiveSkill = OnUseActiveSkill

return Skill_10253