--- 后排队友攻击或者使用技能时，提升自己${Params[1]}%的行动值

local Skill_10274 = BaseClass("Skill_10274",Skill)
local base = Skill

local function __init(self)

end

local function OnUnitTurnEndEvent(self, unit)
    base.OnUnitTurnEndEvent(self, unit)
    if self.IsDisabled then
        return
    end

    if unit == self.SrcUnit then
        return
    end

    if not BattleUtil.IsFriend(self.SrcUnit, unit) then
        return
    end

    if not unit:GetComponent("SsUnitMatrixComponent").Row == MatrixRow.Front then
        return
    end

    self.SrcUnit:GetComponent("BattleUnitComponent"):ChangeActValue(self.Params[1])
end

Skill_10274.__init = __init
Skill_10274.OnUnitTurnEndEvent = OnUnitTurnEndEvent

return Skill_10274