--- 对全体敌人造成${Params[1]}%物理伤害，根据自身的当前生命值百分比额外获得最多${Params[2]}%的伤害倍率。

local Skill_10323 = BaseClass("Skill_10323",Skill)
local base = Skill

local function __init(self)
end

local function OnUseActiveSkill(self, targetUnit)
    local result = ActiveSkillResult.New()
    local damageScale = self.Params[1] / 100
    local extraScale = BattleUtil.GetHpPct(self.SrcUnit) * self.Params[2] / 100
    local scale = damageScale + extraScale

    local atk = self.__snc:Get(NumericType.Atk)
    local damageNum = atk * scale / self.MultistageNum
    result.FeixiaoDamage = {
        Type = BattleDamageType.Physical,
        Value = damageNum
    }
    return result
end

Skill_10323.__init = __init
Skill_10323.OnUseActiveSkill = OnUseActiveSkill

return Skill_10323