local ToolTipSevenView = BaseClass("ToolTipSevenView",UIBaseView)
local base = UIBaseView
local this = ToolTipSevenView

local msg_text_path = "Msg"
local title_text_path  = "TitleText"
local confirm_path = "ConfirmButton"

local function OnClickConfirmButton(self)
    self.ctrl:CloseSelf();
    if self.model.data.callBack ~= nil then
        self.model.data.callBack();
    end
end

function this.OnCreate(self)
    base.OnCreate(self);

    self.confirm_btn = self.rc:GetObject(confirm_path)
    UIUtil.AddBtnEvent(self.confirm_btn, function ()
        OnClickConfirmButton(self)
    end)
end

function this.OnLangCreate(self)
    LangUtil.GetSpriteLang(426, function(Sprite) self.confirm_btn.transform:Find("ConfirmText"):GetComponent("Image").sprite = Sprite  end) -- чбошод
    self.msg_text = LangUtil.BindText(self.rc:GetObject(msg_text_path))
    self.title_text = LangUtil.BindText(self.rc:GetObject(title_text_path))
end

function this.OnEnable(self)
    base.OnEnable(self);
    self:OnRefresh();
end

function this.OnRefresh(self)
    self.msg_text.text = self.model.data.message;
    self.title_text.text = self.model.data.title;
end

function this.OnAddListener(self)
    base.OnAddListener(self);
end

function this.OnRemoveListener(self)
    base.OnRemoveListener(self);
end

function this.OnDestroy(self)
    self.back_btn = nil;
    self.confirm_btn = nil;
    self.msg_text = nil;
    self.title_text = nil;
    base.OnDestroy(self);
end

return this

