local UIEquipStateView = BaseClass("UIEquipStateView",UIBaseView);
local base = UIBaseView
local this = UIEquipStateView

local function InitEquipInfo(self)
    local zEquip = Z_Equip[self.equip.TemplateId]
    local label = self.equipInfo:Find("Label")
    local equipName = self.equipInfo:Find("EquipName"):GetComponent("Text")
    local equipItem = self.equipInfo:Find("EquipItemNew")
    local prop = self.equipInfo:Find("PropNum")
    local desc = self.equipInfo:Find("Desc"):GetComponent("Text")
    local holderAvatar = self.equipInfo:Find("Holder/Avatar"):GetComponent("Image")
    local holderName = self.equipInfo:Find("HolderName"):GetComponent("Text")
    local equipHolder = equipItem:Find("Holder/Avatar"):GetComponent("Image")
    
    UIPublic.InitEquipLabel(label,zEquip)
    equipName.text = zEquip.Name
    
    UIPublic.InitEquipDesc(desc,zEquip)
    UIPublic.InitEquipProp(prop,self.equip)
    coroutine.start(function()
        UIPublic.InitEquipItemNew(equipItem, self.equip)
        if self.card then
            holderName.text = LangUtil.GetSysLang(264)..":<color=#FAEDA8>".. Z_Card[self.card.TemplateId].Name .."</color>"
            UIPublic.InitItemIcon(holderAvatar, nil, 2, self.card.TemplateId, {iconId = self.card.SkinId})
            UIPublic.InitItemIcon(equipHolder, nil, 2, self.card.TemplateId,{iconId = self.card.SkinId})
            equipItem:Find("Holder").gameObject:SetActive(true)
        else
            holderName.text = ""
        end

    end)
end

function this.OnLangCreate(self)
    local equipInfo = self.langRc:GetObject("EquipInfo").transform

    LangUtil.BindText(equipInfo:Find("Label/Text"))
    LangUtil.BindText(equipInfo:Find("EquipName"))
    LangUtil.BindText(equipInfo:Find("PropNum/Text"))
    LangUtil.BindText(equipInfo:Find("PropNum/Value"),FontType.All_Number)
    LangUtil.BindText(equipInfo:Find("Desc"))
    LangUtil.BindText(equipInfo:Find("HolderName"))
end

function this.OnCreate(self)
    base.OnCreate(self)
    self.equipInfo = self.rc:GetObject("EquipInfo").transform
    
    local closeBtn = self.rc:GetObject("CancelButton")
    local eventBg = self.rc:GetObject("EventBg")
    
    local func = function()UIManager:GetInstance():CloseWindow(UIWindowNames.UIEquipState) end
    UIUtil.AddBtnEvent(closeBtn,func)
    EventTriggerListener.Get(eventBg).onLuaClick = func
    
end

function this.OnEnable(self)
    base.OnEnable(self);
    self:OnRefresh();
end

function this.OnRefresh(self)
    local info = self.model.info
    self.card = info.card
    self.equip = Game.Registry:NewObject("Equip", info.equip)
    self.equip:AddComponent("EquipAttrComponent")
    InitEquipInfo(self)
end


function this.OnAddListener(self)
    base.OnAddListener(self)
end

function this.OnRemoveListener(self)
    base.OnRemoveListener(self);
end

function this.OnDisable(self)
    base.OnDisable(self);
end

function this.OnDestroy(self)
    base.OnDestroy(self);
end

return this;

