local UISandShopModel = BaseClass("UISandShopModel",UIBaseModel)
local base = UIBaseModel
local this = UISandShopModel;

function this.OnCreate(self)
    base.OnCreate(self);
    self.paid_num = nil;
    self.shop_data = nil;
end

function this.OnEnable(self)
    base.OnEnable(self);
    self:OnRefresh();
end

function this.OnRefresh(self)
    self.paid_num = Game.Scene.Player.StarSand;
    if self.shop_data == nil then
        self.shop_data = {};
        for k,v in table.pairsByKeys(Z_Store) do
            if v.StoreType == StoreType.StarStone then --星砂商店
                table.insert(self.shop_data,v);
            end
        end
    end
end

local function OnPlayerInfoChg(self)
    self:OnRefresh();
    self:UIBroadcast(UIMessageNames.ON_PLAYER_INFO_CHG)
end

function this.OnAddListener(self)
    base.OnAddListener(self);
    self:AddDataListener(DataMessageNames.ON_PLAYER_INFO_CHG,OnPlayerInfoChg);
end

function this.OnRemoveListener(self)
    base.OnRemoveListener(self);
    self:RemoveDataListener(DataMessageNames.ON_PLAYER_INFO_CHG);
end

function this.OnDisable(self)
    base.OnDisable(self);
    self.paid_num = nil;
end

function this.OnDestroy(self)
    self.shop_data = nil;
    base.OnDestroy(self);
end

return this

