local UISkinShopModel = BaseClass("UISkinShopModel",UIBaseModel);
local base = UIBaseModel;
local this = UISkinShopModel;

function this.OnCreate(self)
    base.OnCreate(self);
    self.shop_data = nil;
end

function this.OnEnable(self,info)
    base.OnEnable(self);
    self.info = info
    if info then
        self.modeType = info.type
    else
        self.modeType = nil
    end
    self:OnRefresh();
end

function this.OnRefresh(self)
    self.shop_data = Z_Skin;
    self.currency_num = Game.Scene.Player.PaidStarStone;
    self.player_skins = Game.Scene.Player:GetComponent("SkinComponent").Skins;
end

function this.OnAddListener(self)
    base.OnAddListener(self);
end

local function OnSkinInfoChg(self)
    self.OnRefresh();
    self:UIBroadcast(UIMessageNames.ON_SKIN_INFO_CHG);
end

function this.OnRemoveListener(self)
    base.OnRemoveListener(self);
    self:AddDataListener(DataMessageNames.ON_SKIN_INFO_CHG,OnSkinInfoChg);
end

function this.OnDisable(self)
    base.OnDisable(self);
    self:RemoveDataListener(DataMessageNames.ON_SKIN_INFO_CHG);
end

function this.OnDestroy(self)
    self.shop_data = nil;
    base.OnDestroy(self);
end

return this;
