local UIStarKingShopView = BaseClass("UIStarKingShopView",UIBaseView)
local base = UIBaseView
local this = UIStarKingShopView;

local back_btn_path = "ReturnButton";
local item_prefab_path = "GeneralShopItem";
local content_path = "Content";
local money_text_path = "Text";

local function OnClickBuy(gameObject)
    local bindData = gameObject:GetComponent("BindData");
    local self = bindData:Get("self");
    local zStore = bindData:Get("zStore");
    self.ctrl:SendBuyRequest(zStore);
end

local function InitContent(self,shop_data)
    ListChildPool:GetInstance():ClearContent(self.content);
    coroutine.start(function ()
        for k,v in table.pairsByKeys(shop_data) do
            UIPublic.InitGeneralShoItem(self,v,OnClickBuy)
        end
    end)
end


function this.OnCreate(self)
    base.OnCreate(self);
    self.item_prefab = self.rc:GetObject(item_prefab_path);
    self.content = self.rc:GetObject(content_path);
    self.back_btn = self.rc:GetObject(back_btn_path);
    self.money_text = self.rc:GetObject(money_text_path):GetComponent("Text");
    ButtonListener.Get(self.back_btn).OnClickHandler = self.ctrl.CloseSelf;
    UIUtil.AddBtnSound(self.back_btn);
end

function this.OnEnable(self)
    base.OnEnable(self);
    self:OnRefresh();
end

function this.OnRefresh(self)
    self.money_text.text = "星皇点:"..self.model.paid_num; -- ignoreCN
    InitContent(self,self.model.shop_data);
end

function this.OnAddListener(self)
    base.OnAddListener(self);
    self:AddUIListener(UIMessageNames.ON_PLAYER_INFO_CHG,self.OnRefresh);
end

function this.OnRemoveListener(self)
    base.OnRemoveListener(self);
    self:RemoveUIListener(UIMessageNames.ON_PLAYER_INFO_CHG);
end

function this.OnDisable(self)
    base.OnDisable(self);
    ListChildPool:GetInstance():ClearContent(self.content);
end

function this.OnDestroy(self)
    base.OnDestroy(self);
end

return this
