local UIStoneShopView = BaseClass("UIStoneShopView",UIBaseView)
local base = UIBaseView
local this = UIStoneShopView;

local item_prefab_path = "StarStoneItem";
local content_path = "Content";
local back_btn_path = "ReturnButton";


local function OnClickBuy(gameObject)
    local bindData = gameObject:GetComponent("BindData");
    local self = bindData:Get("self");
    local amount = bindData:Get("amount");
    local price = bindData:Get("price");
    self.ctrl:SendTopUpRequest(amount,price);
end


local function InitContent(self,shop_data)
    
    ListChildPool:GetInstance():ClearContent(self.content);
    coroutine.start(function()
        for k,v in table.pairsByKeys(shop_data) do
            ListChildPool:GetInstance():GetChild("StarStoneItem",self.content,function (object)
                local transform = object.transform;
                local star_num_text = transform:Find("StarNum"):GetComponent("Text");
                local money_num_text = transform:Find("MoneyNum"):GetComponent("Text");
                local buy_btn = transform:Find("Button").gameObject;
                star_num_text.text = v.PaidStarStone .. "有偿星石"; -- ignoreCN
                money_num_text.text = v.Price .. "点数(日元)"; -- ignoreCN
                local bindData = UIUtil.GetButtonDataComponent(buy_btn);
                bindData:Add("amount",v.PaidStarStone);
                bindData:Add("price",v.Price);
                bindData:Add("self",self);
                ButtonListener.Get(buy_btn).OnClickHandler = OnClickBuy;
                UIUtil.AddBtnSound(buy_btn);
            end);
        end
    end)
end

function this.OnCreate(self)
    base.OnCreate(self);

    self.item_prefab = self.rc:GetObject(item_prefab_path);
    self.content = self.rc:GetObject(content_path);
    self.back_btn = self.rc:GetObject(back_btn_path);
    ButtonListener.Get(self.back_btn).OnClickHandler = self.ctrl.CloseSelf;
    UIUtil.AddBtnSound(self.back_btn);
end

function this.OnEnable(self)
    base.OnEnable(self);
    self:OnRefresh();
end

function this.OnRefresh(self)
    InitContent(self,self.model.shop_data);
end

function this.OnAddListener(self)
    base.OnAddListener(self);
end

function this.OnRemoveListener(self)
    base.OnRemoveListener(self);
end

function this.OnDisable(self)
    base.OnDisable(self);
    ListChildPool:GetInstance():ClearContent(self.content);
end

function this.OnDestroy(self)
    self.item_prefab = nil;
    self.content = nil;
    self.back_btn = nil;
    base.OnDestroy(self);
end

return this