local UITaskCtrl = BaseClass("UITaskCtrl",UIBaseCtrl);
local this = UITaskCtrl

function this.CloseSelf()
    UIManager:GetInstance():CloseWindow(UIWindowNames.UITask);
end

function this:TaskSubmitReq(type, id)
    ---@param response protocol.TaskSubmitResp
    PublicRequest.SendRequest(
        PROTOCOL.TaskSubmitReq,
        {
            type = type,
            task_id = id,
        },
        function(response, error)
            UIPublic.OpenRewardUI(response.reward_result)
        end
    )
end

function this:TaskSubmitAllReq(type)
