local UITransitionModel = BaseClass("UITransitionModel",UIBaseModel);
local base = UIBaseModel
local this = UITransitionModel

function this.OnCreate(self)
    base.OnCreate(self);
end

function this.OnEnable(self, data)
    base.OnEnable(self);
    if data == nil then -- 界面过渡
        self.time = 0.2
        self.mode = nil
    else -- 自宅黑屏
        self.time = data.time
        self.mode = data.mode
    end
end

-- 界面过渡触发事件
local function OnUITransit(self, ui_name)
    self.time = 0.2 -- 关闭UI  快一点
    self.mode = nil
    self.ui_name = ui_name
    self:UIBroadcast(UIMessageNames.ON_UI_TRANSIT)
end

function this.OnAddListener(self)
    base.OnAddListener(self);
    self:AddDataListener(DataMessageNames.ON_UI_TRANSIT, OnUITransit)
end

function this.OnRemoveListener(self)
    base.OnRemoveListener(self);
    self:RemoveDataListener(DataMessageNames.ON_UI_TRANSIT)
end

function this.OnDisable(self)
    base.OnDisable(self);
    
end

function this.OnDestroy(self)
    base.OnDestroy(self);
end

return this;
