local UIEffectComponent = BaseClass("UIEffectComponent",Component);
local base = Component
--local prefabPath = "UI/Prefabs/View/UI/PointEffectController.prefab";
local effectPath = "UI/Effects/UI/"
local this = UIEffectComponent

local function Awake(self)
    base.Awake(self)
    if IsNull(self.gameObject) then
       self.gameObject = GameObject("UIEffectRoot")
       GameObject.DontDestroyOnLoad(self.gameObject)
    end
end

function this.LoadEffect(self,effectName)
    local path = effectPath..effectName..".prefab"
    self.effect = GameObjectPool:GetInstance():CoGetGameObjectAsync(path);
    local transform = self.effect.transform
    transform:SetParent(self.gameObject.transform)
    transform.localScale = Vector3.one;
    transform.localPosition = Vector3.zero;
    
    return self.effect
end

function this.Remove(self)
    if not IsNull(self.effect) then
        GameObject.Destroy(self.effect)
    end
    self.effect = nil
end


local function Dispose(self)
    base.Dispose(self)
end

UIEffectComponent.Awake = Awake
UIEffectComponent.Dispose = Dispose

return UIEffectComponent