local BattleFightSoulComponent = BaseClass("BattleFightSoulComponent", Component)
local base = Component

local AttrNameTypeTable = {
    Hp = NumericType.MaxHpAdd,
    Atk = NumericType.AtkAdd,
    Def = NumericType.DefAdd,
    MDef = NumericType.MagicDefAdd,
}

local function Awake(self)
    base.Awake(self)

    self.numeric_component = self.__entity:GetComponent("NumericComponent")

    local fightSoul = self.__entity.FightSoul
    if fightSoul == nil or fightSoul.Id == nil or fightSoul.Id == 0 then
        return
    end

    local zFightSoulLevelGrowth = Z_FightSoulLevelGrowth[fightSoul.Id][fightSoul.Level]
    for name, attrType in pairs(AttrNameTypeTable) do
        self.numeric_component:Inc(attrType, zFightSoulLevelGrowth[name])
    end

end

local function Dispose(self)
    base.Dispose(self)
end

BattleFightSoulComponent.Awake = Awake
BattleFightSoulComponent.Dispose = Dispose

return BattleFightSoulComponent