local SsUnitMatrixComponent = BaseClass("SsUnitMatrixComponent", Component)
local base = Component

local function Awake(self,zMatrix)
    base.Awake(self)
    self.MatrixPos = zMatrix.Desc
    self.GameObject = GameObject.Find(zMatrix.GameObject)
    self.Group = zMatrix.Group
    self.Row = zMatrix.Row
    self.Line = zMatrix.Line
    
    self.battle_component = Game.Scene:GetComponent("BattleComponent")
end

local function GetEnemyMatrixGroup(self)
    if self.Group == MatrixGroup.Friend then
        return MatrixGroup.Enemy
    else
        return MatrixGroup.Friend
    end
end

local function GetGroup(self)
    if self.Group == MatrixGroup.Friend then
        return self.battle_component.FriendGroup
    else
        return self.battle_component.EnemyGroup
    end
end

local function Dispose(self)
    base.Dispose(self)
end

SsUnitMatrixComponent.Awake = Awake
SsUnitMatrixComponent.GetEnemyMatrixGroup = GetEnemyMatrixGroup
SsUnitMatrixComponent.GetGroup = GetGroup
SsUnitMatrixComponent.Dispose = Dispose

return SsUnitMatrixComponent