local Group = BaseClass("Group",Entity)
local base = Entity
--                        敌方或我方   战阵模板Id             阵型强化模板Id                        
local function Awake(self,matrixGroup,battleArrayTemplateId,formationEnhanceTemplateId)
    base.Awake(self)
    
    self.MatrixGroup = matrixGroup
    self.Energy = 0
    self.BattleArrayTemplateId = battleArrayTemplateId or 0
    self.FormationEnhanceTemplateId = formationEnhanceTemplateId or 0
    ------------------------------------------------------------------------
end

local function GetEnemyMatrixGroup(self) --获取敌方
    if self.MatrixGroup == MatrixGroup.Friend then
        return MatrixGroup.Enemy
    else
        return MatrixGroup.Friend
    end
end

local function Dispose(self)
    base.Dispose(self)
end

Group.Awake = Awake
Group.GetEnemyMatrixGroup = GetEnemyMatrixGroup
Group.Dispose = Dispose

return Group