local function OnActStart(self)
    base.OnActStart(self)

    if self.__bc.CurActSkill.ZSkill == nil then
        return
    end
    if self.__bc.CurActSkill.ZSkill.SkillType ~= BattleSkillType.Assist then
        return
    end

