local Skill_10023 = BaseClass("Skill_10023",Skill)
local base = Skill

local function __init(self)
end

local function OnUseActiveSkill(self, targetUnit)
    local result = ActiveSkillResult.New()
    if targetUnit == self.SrcUnit then
        return result
    end

    local damageScale = self.Params[1] / 100
    local actValue = self.Params[2]

    if targetUnit:GetComponent("BuffComponent"):HasBuffId(BattleBuffType.Freeze) then
        damageScale = damageScale + self.Params[3] / 100
        actValue =  actValue + self.Params[4]
    end

    local atk = self.__snc:Get(NumericType.Atk)
    local damageNum = atk * damageScale / self.MultistageNum
    result.FeixiaoDamage = {
        Type = BattleDamageType.Physical,
        Value = damageNum
    }

    self.SrcUnit:GetComponent("BattleUnitComponent"):ChangeActValue(actValue)

    return result
end

Skill_10023.__init = __init
Skill_10023.OnUseActiveSkill = OnUseActiveSkill

return Skill_10023