local function OnUseActiveSkill(self,targetUnit)
    local result = ActiveSkillResult.New()
    local count = targetUnit:GetComponent("BuffComponent"):BuffIdCount(BattleBuffType.Fire)
    local extraScale = count * self.Params[2] / 100
