local function OnUnitTurnStartEvent(self, unit)
    base.OnUnitTurnStartEvent(self, unit)
    if self.IsDisabled then
        return
    end
    if BattleUtil.IsFriend(self.SrcUnit, unit) then
        return
    end
    if not unit:GetComponent("BuffComponent"):HasBuffId(BattleBuffType.Paralysis) then
        return
    end
    local friends = self:__get_friend_all()
    local hp = self.__snc:Get(NumericType.Atk) * self.Params[1] / 100
    table.walk(friends, function(k, v)
        v:GetComponent("SsUnitBattleComponent"):HealHp(hp)
    end)
end

