local UIBattleAwake = BaseClass("UIBattleAwake", UIBaseView)
local this = UIBattleAwake
local base = UIBaseView

function this.OnCreate(self)
    base.OnCreate(self)
    LangUtil.BindText(self.rc:GetObject("Name")).text = LangUtil.GetSysLang(3024)
    self.content = LangUtil.BindText(self.rc:GetObject("Content"))
end

function this.OnEnable(self)
    base.OnEnable(self)
    local skill = Game.Scene:GetComponent("BattleComponent").CurActSkill
    if skill ~= nil then
        self.content.text = skill.Name
    end
end

function this.OnDisable(self)
    base.OnDisable(self)
end

function this.OnDestroy(self)
    base.OnDestroy(self)
end

return this