---@class ToolTipFirstView : UIBaseModel
---@field model ToolTipFirstModel
---@field ctrl ToolTipFirstCtrl
local ToolTipFirstView = BaseClass("ToolTipFirstView",UIBaseView)
local base = UIBaseView
local this = ToolTipFirstView


local function OnClickConfirmButton(self)
    self.ctrl.CloseSelf();
    if self.model.data.callBack ~= nil then
        self.model.data.callBack()
    end
end

local function OnClickCancelButton(self)
    self.ctrl.CloseSelf();
    if self.model.data.cancelCallBack ~= nil then
        self.model.data.cancelCallBack();
    end
end

local function OnClickCloseButton(self)
    self.ctrl.CloseSelf();
    if self.model.data.closeCallBack ~= nil then
        self.model.data.closeCallBack()
    end
end

function this.OnCreate(self)
    base.OnCreate(self);

    self.cancel_btn = self.rc:GetObject("Cancel");
    self.confirm_btn = self.rc:GetObject("ConfirmButton");
    self.eventBg = self.rc:GetObject("EventBg")

    UIUtil.AddBtnEvent(self.cancel_btn,function() OnClickCancelButton(self) end)
    UIUtil.AddBtnEvent(self.confirm_btn,function()OnClickConfirmButton(self) end)
    UIUtil.AddBtnEvent(self.eventBg,function()OnClickCloseButton(self) end)
end

function this.OnLangCreate(self)
    self.cancel_btn:SetActive(not self.model.data.hideBackBtn)
    self.confirm_btn:SetActive(not self.model.data.hideConfirmBtn)
    LangUtil.GetSpriteLang(426, function(Sprite) self.confirm_btn.transform:Find("ConfirmText"):GetComponent("Image").sprite = Sprite  end) -- 确认
    self.msg_text = LangUtil.BindText(self.rc:GetObject("Msg"))
    self.title_text = LangUtil.BindText(self.rc:GetObject("TitleText"))
end

function this.OnEnable(self)
    base.OnEnable(self);
    self:OnRefresh();
end

function this.OnRefresh(self)
    self.msg_text.alignment = self.model.data.alignment or 4
    self.msg_text.text = self.model.data.message;
    self.title_text.text = self.model.data.title;
    if string.IsNullOrEmpty(self.model.data.title) then
        self.title_text.text = LangUtil.GetSysLang(574) -- 提示
    end

    local image = self.cancel_btn.transform:Find("CancelText"):GetComponent("Image")
    if self.model.data.cancenSysLangKsy then
        LangUtil.GetSpriteLang(self.model.data.cancenSysLangKsy, function(Sprite) image.sprite = Sprite  end) -- 取消
    else
        LangUtil.GetSpriteLang(507, function(Sprite) image.sprite = Sprite  end) -- 取消
    end
    image:SetNativeSize()
end

function this.OnAddListener(self)
    base.OnAddListener(self);
end

function this.OnRemoveListener(self)
    base.OnRemoveListener(self);
end

function this.OnDisable(self)
    base.OnDisable(self)
end

function this.OnDestroy(self)
    self.cancel_btn = nil;
    self.confirm_btn = nil;
    self.msg_text = nil;
    self.title_text = nil;
    base.OnDestroy(self);
end

return this

