local UIFightSoulFuseView = BaseClass("UIFightSoulFuseView",UIBaseView);
local base = UIBaseView
local this = UIFightSoulFuseView

local function InitFightSoulFace(self)
    local fightSoul = self.fightSoul
    local zFightSoul = Z_FightSoul[fightSoul.TemplateId]

    local transform = self.fightSoulFace.transform 
    local rare = zFightSoul.Rare

    local face = transform:Find("Mask/Face"):GetComponent("SpriteRenderer")
    local frame = transform:Find("Frame"):GetComponent("SpriteRenderer")
    local sub = transform:Find("Sub"):GetComponent("SpriteRenderer")

    self.Name.text = zFightSoul.Name
    local class = UIPublic.GetFightSoulClass(zFightSoul.ClassLimit)
    coroutine.start(function()
        self.line:GetComponent("SpriteRenderer").sprite = AtlasManager:GetInstance():CoLoadImageAsync("UI/Common/CardInfo/".. UIPublic.RareNames[rare] .."_Line.png")
        UIUtil.SetFightSoulSprite(face,zFightSoul.Id,FightSoulPictureType.Big)
        UIUtil.SetSprite(sub, AtlasConfig.Class, class .. "_" .. zFightSoul.Rare .. ".png")
        frame.sprite = AtlasManager:GetInstance():CoLoadImageAsync("UI/FightSoulFrame/".. RareConfig[fightSoul.config.Rare])
    end)
end

local function OnSkip(self)
    UIManager:GetInstance():OpenWindow(UIWindowNames.UIFightSoulFuseStage1,{fightSoul = self.fightSoul})
    coroutine.start(function()
        coroutine.waitforseconds(0.5)
        UIManager:GetInstance():CloseWindow(UIWindowNames.UIFightSoulFuse)
    end)
end

function this.OnCreate(self)
    base.OnCreate(self);
    self.eventBg = self.rc:GetObject("EventBg")
    self.effect = self.rc:GetObject("Effect")
    self.fightSoulFace = self.rc:GetObject("FightSouFace")
    self.mask = self.rc:GetObject("Mask")
    self.audio = self.gameObject:GetComponent("AudioSource")
    -- self.SubName = self.rc:GetObject("SubName"):GetComponent("Text")
    self.Name = self.rc:GetObject("Name"):GetComponent("Text")
    -- self.fightT = self.rc:GetObject("fightT"):GetComponent("Text")
    self.line= self.rc:GetObject("line").gameObject
    self.cardEffect = self.rc:GetObject("Card1")
    self.cardEffectImg = self.rc:GetObject("CardEffectImage"):GetComponent("RawImage")
    self.cardEffect.transform:GetComponent("Renderer").material.mainTexture = self.cardEffectImg.texture
end

function this.OnEnable(self)
    base.OnEnable(self);
    self:OnRefresh();
end



function this.OnRefresh(self)
    local starRoot = self.fightSoulFace.transform:Find("StarRoot"):GetComponent("SpriteRenderer")

    -- self.SubName.gameObject:SetActive(false)
    self.Name.gameObject:SetActive(false)
    -- self.fightT.gameObject:SetActive(false)
    self.line:SetActive(false)
    LangUtil.BindText(self.Name)
    -- LangUtil.BindText(self.fightT).text = LangUtil.GetSysLang(413)
    self.fightSoul = self.model.info.fightSoul
    self.audio.volume = ClientData:GetInstance():GetVolume("SoundVolume")
    self.mask:SetActive(true)
    InitFightSoulFace(self)
    coroutine.start(function()
        starRoot.sprite = AtlasManager:GetInstance():CoLoadImageAsync("UI/Common/CardInfo/Star_".. self.model.info.oldStar ..".png")
    end)
    self.mask:SetActive(false)
    self.audio:Play()
    coroutine.waitforseconds(0.6)
    UIPublic.PlayEffect(self.effect.transform)
    -- self.SubName.gameObject:SetActive(true)
    self.Name.gameObject:SetActive(true)
    -- self.fightT.gameObject:SetActive(true)
    self.line.gameObject:SetActive(true)
    -- self.SubName:DOFade(0,3.5)
    self.Name:DOFade(0,3.5)
    -- self.fightT:DOFade(0,3.5)
    self.line.gameObject:GetComponent("SpriteRenderer"):DOFade(0,3)
    coroutine.waitforseconds(3.5)
    --self.SubName.gameObject:SetActive(false)
    --self.Name.gameObject:SetActive(false)
    --self.fightT.gameObject:SetActive(false)
    --self.line.gameObject:SetActive(false)
    
    self.transform:DOShakePosition(0.6,40,80,90);
    UIUtil.OpenSkipHint(function()OnSkip(self)end)
    coroutine.waitforseconds(0.7)

    coroutine.start(function()
        starRoot.sprite = AtlasManager:GetInstance():CoLoadImageAsync("UI/Common/CardInfo/Star_".. self.model.info.Star ..".png")
    end)

    coroutine.waitforseconds(0.8)
    --self.SubName.gameObject:SetActive(true)
    --self.Name.gameObject:SetActive(true)
    --self.fightT.gameObject:SetActive(true)
    --self.line.gameObject:SetActive(true)
    -- self.SubName:DOFade(1,0)

    self.Name:DOFade(1,0)
    -- self.fightT:DOFade(1,0)
    self.line.gameObject:GetComponent("SpriteRenderer"):DOFade(1,0)
    
end


function this.OnAddListener(self)
