local UIRewardModel = BaseClass("UIRewardModel",UIBaseModel);
local base = UIBaseModel
local this = UIRewardModel



function this.OnCreate(self)
    base.OnCreate(self);
   
end
---@param info {rewards:{num:number, RewardType:number, templateId:number}[], closeCallBack:function}
function this.OnEnable(self,info)
    base.OnEnable(self)
    local rewards = {}
    local rewardMap = {}
    for _, value in ipairs(info.rewards) do
        if rewardMap[value.RewardType] == nil then
            rewardMap[value.RewardType] = {}
        end
        if rewardMap[value.RewardType][value.templateId] == nil then
            rewardMap[value.RewardType][value.templateId] = value.num
        else
            rewardMap[value.RewardType][value.templateId] = rewardMap[value.RewardType][value.templateId] + value.num
        end
    end

    for rewardType, value in pairs(rewardMap) do
        for templateId, num in pairs(value) do
            if rewardType ~= RewardType.RewardTypeHeadPortrait
                    and rewardType ~= RewardType.RewardTypeCharacterSkin
                    and rewardType ~= RewardType.RewardTypePassProgress
                    and rewardType ~= RewardType.RewardTypePassProgressWithAddition
                    then
                table.insert(
                    rewards,
                    {
                        num = num,
                        RewardType = rewardType,
                        templateId = templateId,
                    }
                )
            elseif rewardType == RewardType.RewardTypeCharacterSkin then
                local zSkin = Z_Skin[templateId]
                if zSkin ~= nil and zSkin.IsShowInRewardUI == 1 then
                    table.insert(
                        rewards,
                        {
                            num = num,
                            RewardType = rewardType,
                            templateId = templateId,
                        }
                    )
                end
            end
        end
    end
    self.titleKey = info.titleKey
    self.rewards = rewards
    self.closeCallBack = info.closeCallBack
    self:OnRefresh();
end

