local UIPropsForShopView = BaseClass("UIPropsForShopView",UIBaseView)
local base = UIBaseView
local this = UIPropsForShopView;

local back_btn_path = "ReturnButton";
local item_prefab_path = "GeneralShopItem";
local content_path = "Content";
local money_text_path = "Text";


local function OnClickBuy(gameObject)
    local bindData = gameObject:GetComponent("BindData");
    local self = bindData:Get("self");
    local zExchangeStore = bindData:Get("zExchangeStore");
    self.ctrl:SendBuyRequest(zExchangeStore);
end

local function InitContent(self,shop_data)
    ListChildPool:GetInstance():ClearContent(self.content);
    coroutine.start(function()
        for k,v in table.pairsByKeys(shop_data) do
            local z_item = Z_Item[v.ItemId]
            ListChildPool:GetInstance():GetChild("GeneralShopItem",self.content,function (object)
                local transform = object.transform;
                local icon = transform:Find("Icon"):GetComponent("Image");
                local name = transform:Find("Name"):GetComponent("Text");
                local desc_text = transform:Find("Des"):GetComponent("Text");
                local price_text = transform:Find("Price"):GetComponent("Text");
                local buy_btn = transform:Find("Button").gameObject;

                local str = "";

                for i = 1,#v.ExpendItem do
                    local z_item_1 = Z_Item[v.ExpendItem[i]];
                    str = str .. z_item_1.Name .. "x".. v.ExpendItemNum[i] .. " ";
                end
                UIUtil.SetSprite(icon,AtlasConfig.Item,GetItemIcon(z_item));
                price_text.text = "";
                name.text = z_item.Name .."x".. v.Amount ;
                desc_text.text = z_item.Desc .."\n".. str;
                buy_btn.transform:Find("Text"):GetComponent("Text").text = "兑换"; -- ignoreCN
                local bindData = UIUtil.GetButtonDataComponent(buy_btn);
                bindData:Add("self",self);
                bindData:Add("zExchangeStore",v);
                ButtonListener.Get(buy_btn).OnClickHandler = OnClickBuy;
                UIUtil.AddBtnSound(buy_btn);
            end);
        end
    end)
end


function this.OnCreate(self)
    base.OnCreate(self);
    self.item_prefab = self.rc:GetObject(item_prefab_path);
    self.content = self.rc:GetObject(content_path);
    self.back_btn = self.rc:GetObject(back_btn_path);
    --self.money_text = self.rc:GetObject(money_text_path):GetComponent("Text");
    ButtonListener.Get(self.back_btn).OnClickHandler = self.ctrl.CloseSelf;
    UIUtil.AddBtnSound(self.back_btn);
end

function this.OnEnable(self)
    base.OnEnable(self);
    self:OnRefresh();
end

function this.OnRefresh(self)
    --self.money_text.text = "有偿星石:"..self.model.paid_num;
    InitContent(self,self.model.shop_data);
end

function this.OnAddListener(self)
    base.OnAddListener(self);
    self:AddUIListener(UIMessageNames.ON_PLAYER_INFO_CHG,self.OnRefresh);
end

function this.OnRemoveListener(self)
    base.OnRemoveListener(self);
    self:RemoveUIListener(UIMessageNames.ON_PLAYER_INFO_CHG);
end

function this.OnDisable(self)
    base.OnDisable(self);
    ListChildPool:GetInstance():ClearContent(self.content);
end

function this.OnDestroy(self)
    base.OnDestroy(self);
end

return this
