local UIShowPropModel = BaseClass("UIShowPropModel",UIBaseModel);
local base = UIBaseModel;
local this = UIShowPropModel;

function this.OnCreate(self)
    base.OnCreate(self);
end

function this.OnEnable(self,data)
    base.OnEnable(self);
    self:OnRefresh(data);
end

function this.OnRefresh(self,data)
    local Items = Game.Scene.Player:GetComponent("ItemComponent").Items;
    self.data = data
    self.item_list = {};

    if self.data.Type == 0 then
        self.item_list = Items;
        return;
    end

    for k,v in table.pairsByKeys(Items) do
        local z_item = Z_Item[v.TemplateId];
        if tonumber(z_item.Type) == self.data.Type then
            self.item_list[k] = v;
        end
    end
end

function this.OnAddListener(self)
    base.OnAddListener(self);
end

function this.OnRemoveListener(self)
    base.OnRemoveListener(self);
end

function this.OnDisable(self)
    base.OnDisable(self);
end

function this.OnDestroy(self)
    base.OnDestroy(self);
end

return this;