local UIStoryChapterModel = BaseClass("UIStoryChapterModel",UIBaseModel);
local base = UIBaseModel
local this = UIStoryChapterModel



function this.OnCreate(self)
    base.OnCreate(self);
   
end

function this.OnEnable(self,data)
    if data ~= nil then
        self.mode = data.mode
        self.kind = data.kind
    else
        self.mode = nil
        self.kind = nil
    end
    if self.kind == nil then--1.主线、2.活动
        local chapterId = ClientData:GetInstance().CurChapterId
        if chapterId > 1000 then
            self.kind = 2
        else
            self.kind = 1
        end
    end
    base.OnEnable(self);
    self:OnRefresh();
end

function this.OnRefresh(self)

end
